import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/contexts/theme-context";
import Home from "@/pages/home.tsx";
import ResumeHub from "@/pages/resume-hub";
import AssistantPMResume from "@/pages/assistant-pm-resume";
import ITAssistantPMResume from "@/pages/it-assistant-pm-resume";
import PMInternshipResume from "@/pages/pm-internship-resume";
import AdminLogin from "@/pages/admin-login";
import AdminContacts from "@/pages/admin-contacts";
import AdminDashboard from "@/pages/admin-dashboard";
import AdminSettings from "@/pages/admin-settings";
import AdminSecurityTest from "@/pages/admin-security-test";
import AdminProtectedRoute from "@/components/AdminProtectedRoute";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/admin/resume">
        <AdminProtectedRoute>
          <ResumeHub />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/resume/assistant-pm">
        <AdminProtectedRoute>
          <AssistantPMResume />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/resume/it-assistant-pm">
        <AdminProtectedRoute>
          <ITAssistantPMResume />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/resume/pm-internship">
        <AdminProtectedRoute>
          <PMInternshipResume />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/login" component={AdminLogin} />
      <Route path="/admin">
        <AdminProtectedRoute>
          <AdminDashboard />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/contacts">
        <AdminProtectedRoute>
          <AdminContacts />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/settings">
        <AdminProtectedRoute>
          <AdminSettings />
        </AdminProtectedRoute>
      </Route>
      <Route path="/admin/security">
        <AdminProtectedRoute>
          <AdminSecurityTest />
        </AdminProtectedRoute>
      </Route>
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <TooltipProvider>
          {/* Skip link for accessibility */}
          <a 
            href="#main-content" 
            className="sr-only focus:not-sr-only focus:absolute focus:top-2 focus:left-2 bg-primary-blue text-white px-4 py-2 rounded z-50 focus:z-50"
            tabIndex={1}
          >
            Skip to main content
          </a>
          <Toaster />
          <Router />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

