  --divider-gray: hsl(0, 0%, 25%); /* #404040 - Dark borders */
  --nav-background: hsl(0, 0%, 10%); /* #1A1A1A - Dark navigation */
  --soft-neutral-white: hsl(0, 0%, 7%); /* Dark background */

  /* Dark mode gradients */
  --hero-gradient: linear-gradient(135deg, 
    hsl(0, 0%, 8%) 0%,
    hsl(0, 0%, 12%) 100%);
  --section-gradient: linear-gradient(135deg, hsl(0, 0%, 7%) 0%, hsl(0, 0%, 10%) 100%);
  --card-gradient: linear-gradient(135deg, hsl(0, 0%, 12%) 0%, hsl(0, 0%, 8%) 100%);
}

/* Modern Professional Animations */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes pulse {
  0%, 100% {
    transform: scale(1);
  }
  50% {
    transform: scale(1.05);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: 200px 0;
  }
}

/* Professional IT PM Utility Classes */
.bg-hero-gradient { background: var(--hero-gradient); }
.bg-professional-gradient { background: var(--professional-gradient); }
.text-gradient {
  background: var(--primary-gradient);
  background-clip: text;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
}

.hover-glow-teal:hover { box-shadow: var(--glow-teal); }
.hover-glow-purple:hover { box-shadow: var(--glow-purple); }
.hover-glow-coral:hover { box-shadow: var(--glow-coral); }
.hover-glow-gold:hover { box-shadow: var(--glow-gold); }

/* Hero Animation Classes */
.animate-slide-in-left {
  animation: slideInLeft 0.8s ease-out forwards;
}

.animate-slide-in-right {
  animation: slideInRight 0.8s ease-out forwards;
}

.animation-delay-200 {
  animation-delay: 0.2s;
  opacity: 0;
}

.dark {
  --background: hsl(222, 84%, 4.9%);
  --foreground: hsl(210, 40%, 98%);
  --muted: hsl(217, 32%, 17%);
  --muted-foreground: hsl(215, 16%, 57%);
  --popover: hsl(222, 84%, 4.9%);
  --popover-foreground: hsl(210, 40%, 98%);
  --card: hsl(222, 84%, 4.9%);
  --card-foreground: hsl(210, 40%, 98%);
  --border: hsl(217, 32%, 17%);
  --input: hsl(217, 32%, 17%);
  --primary: hsl(210, 40%, 98%);
  --primary-foreground: hsl(222, 84%, 4.9%);
  --secondary: hsl(217, 32%, 17%);
  --secondary-foreground: hsl(210, 40%, 98%);
  --accent: hsl(217, 32%, 17%);
  --accent-foreground: hsl(210, 40%, 98%);
  --destructive: hsl(0, 62.8%, 30.6%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --ring: hsl(212, 12%, 84%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground transition-colors duration-300;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  /* Professional Card Style - Modern IT PM Standard */
  .professional-card {
    @apply bg-card rounded-xl border border-divider-gray transition-all duration-300 ease-in-out;
    box-shadow: 0 2px 12px rgba(0, 0, 0, 0.04);
    padding: 1.5rem;
  }

  /* Dark mode responsive professional components */
  .dark .professional-card {
    @apply bg-gray-800 border-gray-700;
    color: #F5F5F5;
  }

  .dark .section-header {
    color: #F5F5F5;
  }

  .dark .section-subtitle,
  .dark .professional-status-item {
    color: #D1D5DB;
  }

  /* Search result line clamping */
  .line-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* Performance optimizations */
  * {
    scroll-behavior: smooth;
  }

  /* Modern visual enhancements */
  .modern-card-hover {
    transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    backface-visibility: hidden;
    transform: translateZ(0);
  }

  .modern-card-hover:hover {
    transform: translateY(-2px) scale(1.02);
    box-shadow: 0 10px 25px -5px rgba(44, 115, 210, 0.1), 0 10px 10px -5px rgba(44, 115, 210, 0.04);
  }

  .dark .modern-card-hover:hover {
    transform: translateY(-4px) scale(1.02);
    box-shadow: 0 15px 35px -5px rgba(0, 0, 0, 0.4), 0 10px 15px -5px rgba(0, 0, 0, 0.2);
    background-color: transparent !important;
  }

  /* Consistent dark mode elevation effects for all interactive elements */
  .dark button:hover,
  .dark .hover\:scale-105:hover,
  .dark .hover\:translate-y-\[-1px\]:hover {
    box-shadow: 0 4px 12px -2px rgba(0, 0, 0, 0.3), 0 2px 6px -2px rgba(0, 0, 0, 0.1);
  }

  /* Subtle micro-interactions */
  .micro-bounce {
    transition: transform 0.2s cubic-bezier(0.68, -0.55, 0.265, 1.55);
  }

  .micro-bounce:hover {
    transform: scale(1.05);
  }

  /* Enhanced focus states for accessibility */
  .focus-enhanced:focus {
    outline: 2px solid #2C73D2;
    outline-offset: 2px;
    box-shadow: 0 0 0 4px rgba(44, 115, 210, 0.1);
  }

  /* Smooth loading states */
  .skeleton-loader {
    background: linear-gradient(90deg, #f0f0f0 25%, #e0e0e0 50%, #f0f0f0 75%);
    background-size: 200% 100%;
    animation: skeleton-loading 1.5s infinite;
  }

  .dark .skeleton-loader {
    background: linear-gradient(90deg, #374151 25%, #4b5563 50%, #374151 75%);
    background-size: 200% 100%;
  }

  @keyframes skeleton-loading {
    0% { background-position: 200% 0; }
    100% { background-position: -200% 0; }
  }

  /* Force navigation inline layout */
  .flex-nowrap {
    flex-wrap: nowrap !important;
  }

  /* Animated gradient text flow effect - runs once */
  .gradient-text-flow {
    background: linear-gradient(90deg, #1E1E1E 0%, #2C73D2 30%, #43D8C9 70%, #1E1E1E 100%);
    background-size: 300% 100%;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    animation: flowGradientOnce 4s ease-in-out forwards;
  }

  .dark .gradient-text-flow {
    background: linear-gradient(90deg, #F5F5F5 0%, #2C73D2 30%, #43D8C9 70%, #F5F5F5 100%);
    background-size: 300% 100%;
    background-clip: text;
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    animation: flowGradientOnceDark 4s ease-in-out forwards;
  }

  @keyframes flowGradientOnceDark {
    0% {
      background-position: 0% 50%;
    }
    70% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 100% 50%;
      -webkit-text-fill-color: #F5F5F5;
    }
  }

  .gradient-text-flow-title {
    background: linear-gradient(90deg, #6B7280 0%, #2C73D2 35%, #43D8C9 65%, #6B7280 100%);
    background-size: 300% 100%;
