    color: #9CA3AF;
  }

  .toc-list {
    list-style: none;
    margin: 0;
    padding: 0;
  }

  .toc-item {
    margin: 0;
  }

  .toc-link {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    width: 100%;
    padding: 0.375rem 0.75rem;
    background: none;
    border: none;
    text-align: left;
    font-size: 0.8rem;
    font-weight: 500;
    color: #64748B;
    transition: all 0.2s ease;
    cursor: pointer;
    position: relative;
  }

  .dark .toc-link {
    color: #9CA3AF;
  }

  .toc-link:hover {
    color: #2C73D2;
    background: rgba(44, 115, 210, 0.05);
  }

  .dark .toc-link:hover {
    color: #60A5FA;
    background: rgba(96, 165, 250, 0.1);
  }

  .toc-link.active {
    color: #2C73D2;
    background: rgba(44, 115, 210, 0.1);
    border-right: 3px solid #2C73D2;
