  }

  .hover-glow-primary {
    @apply transition-all duration-300 ease-in-out;
  }

  .hover-glow-primary:hover {
    box-shadow: 0 0 0 2px rgba(44, 115, 210, 0.15);
  }

  /* Enhanced Accessibility Features */
  .sr-only {
    position: absolute;
    width: 1px;
    height: 1px;
    padding: 0;
    margin: -1px;
    overflow: hidden;
    clip: rect(0, 0, 0, 0);
    white-space: nowrap;
    border: 0;
  }

  .focus\:not-sr-only:focus {
    position: static;
    width: auto;
    height: auto;
    padding: 0.5rem 1rem;
    margin: 0;
    overflow: visible;
    clip: auto;
    white-space: normal;
  }

  /* High Contrast Focus Indicators */
  button:focus,
  a:focus,
  input:focus,
  textarea:focus,
  select:focus {
    outline: 2px solid var(--focus-ring) !important;
    outline-offset: 2px !important;
  }

  /* Reduced Motion Support */
  @media (prefers-reduced-motion: reduce) {
    *,
    *::before,
    *::after {
      animation-duration: 0.01ms !important;
      animation-iteration-count: 1 !important;
      transition-duration: 0.01ms !important;
      scroll-behavior: auto !important;
    }
  }
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.8;
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes shimmer {
  0% {
    background-position: -200px 0;
  }
  100% {
    background-position: calc(200px + 100%) 0;
  }
}

@keyframes progressPulse {
  0%, 100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.8;
    transform: scale(1.02);
  }
}

/* Glassmorphism Effect */
.glassmorphism {
  background: rgba(255, 255, 255, 0.25);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border: 1px solid rgba(255, 255, 255, 0.3);
}

.dark .glassmorphism {
  background: rgba(31, 41, 55, 0.6);
  border: 1px solid rgba(75, 85, 99, 0.3);
}

.glassmorphism:hover {
  background: rgba(255, 255, 255, 0.35);
  border: 1px solid rgba(44, 115, 210, 0.2);
  transform: translateY(-2px);
}

.dark .glassmorphism:hover {
  transform: translateY(-4px);
  box-shadow: 0 15px 35px -5px rgba(0, 0, 0, 0.4), 0 10px 15px -5px rgba(0, 0, 0, 0.2);
  background: rgba(31, 41, 55, 0.6) !important;
}

/* Progress Ring Animation */
.progress-ring {
  animation: progressPulse 3s ease-in-out infinite;
}

/* In Progress Badge Shimmer */
.badge-progress-shimmer {
  background: linear-gradient(
    90deg,
    #FFF8E1 0px,
    #FFF3C4 40px,
    #FFECB3 80px,
    #FFF8E1 120px
  );
  background-size: 200px 100%;
  animation: shimmer 2s infinite linear;
}

/* Scroll Snap for Mobile */
@media (max-width: 768px) {
  .scroll-snap-x {
    display: flex;
    overflow-x: auto;
    scroll-snap-type: x mandatory;
    gap: 1rem;
    padding: 0 1rem;
    -webkit-overflow-scrolling: touch;
  }

  .scroll-snap-x > * {
    flex: 0 0 85%;
    scroll-snap-align: center;
  }

  .scroll-snap-x::-webkit-scrollbar {
    height: 4px;
  }

  .scroll-snap-x::-webkit-scrollbar-track {
    background: rgba(255, 255, 255, 0.1);
    border-radius: 2px;
  }

  .scroll-snap-x::-webkit-scrollbar-thumb {
    background: rgba(44, 115, 210, 0.5);
    border-radius: 2px;
  }
}

/* Animated Entry */
@keyframes slideInFromBottom {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes slideInFromLeft {
  from {
    opacity: 0;
    transform: translateX(-30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

@keyframes slideInFromRight {
  from {
    opacity: 0;
    transform: translateX(30px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.animate-slide-in-bottom {
  animation: slideInFromBottom 0.8s ease-out forwards;
}

.animate-slide-in-left {
  animation: slideInFromLeft 0.8s ease-out forwards;
}

.animate-slide-in-right {
  animation: slideInFromRight 0.8s ease-out forwards;
}

.animation-delay-100 { animation-delay: 0.1s; }
.animation-delay-200 { animation-delay: 0.2s; }
.animation-delay-300 { animation-delay: 0.3s; }
.animation-delay-400 { animation-delay: 0.4s; }
