                  className="h-8 w-8 border border-primary-blue bg-white dark:bg-gray-800 text-primary-blue hover:bg-primary-blue hover:text-white"
                  aria-label="Open mobile menu"
                  data-testid="mobile-menu-button"
                >
                  <Menu className="h-4 w-4" />
                </Button>
              </SheetTrigger>
              <SheetContent side="right" className="w-80">
                <div className="flex flex-col space-y-6 mt-8">
                  {navItems.map((item) => (
                    <button
                      key={item.id}
                      onClick={() => scrollToSection(item.id)}
                      className={`text-left text-lg font-medium transition-colors duration-200 ${
                        activeSection === item.id 
                          ? "text-primary font-semibold" 
                          : "text-foreground hover:scale-105 hover:translate-y-[-1px] transition-all duration-200"
                      }`}
                    >
                      {item.label}
                    </button>
                  ))}
                </div>
              </SheetContent>
            </Sheet>
          </div>
        </div>
      </div>

      {/* Search Modal */}
      <SearchBar isOpen={isSearchOpen} onClose={closeSearch} />
    </nav>
  );
};

export default Navigation;

