client/src/components/world-clock.tsx.content:
import React, { useState, useEffect } from 'react';
import { Clock } from 'lucide-react';

interface TimeZone {
  label: string;
  timezone: string;
  code: string;
}

const WorldClock: React.FC = () => {
  const [times, setTimes] = useState<{ [key: string]: string }>({});

  const timezones: TimeZone[] = [
    { label: 'Local', timezone: 'America/Los_Angeles', code: 'PST' },
    { label: 'Eastern', timezone: 'America/New_York', code: 'EST' },
    { label: 'UTC', timezone: 'UTC', code: 'UTC' }
  ];

  useEffect(() => {
    const updateTimes = () => {
      const newTimes: { [key: string]: string } = {};
      const now = new Date();

      // PST (Pacific Standard Time)
      const pstTime = new Intl.DateTimeFormat('en-US', {
        timeZone: 'America/Los_Angeles',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      }).format(now);
      newTimes['PST'] = pstTime;

      // EST (Eastern Standard Time)
      const estTime = new Intl.DateTimeFormat('en-US', {
        timeZone: 'America/New_York',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      }).format(now);
      newTimes['EST'] = estTime;

      // UTC
      const utcTime = new Intl.DateTimeFormat('en-US', {
        timeZone: 'UTC',
        hour: '2-digit',
        minute: '2-digit',
        hour12: false
      }).format(now);
      newTimes['UTC'] = utcTime;

      setTimes(newTimes);
    };

    // Update immediately
    updateTimes();

    // Update every minute
    const interval = setInterval(updateTimes, 60000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="flex items-center gap-4 text-sm text-gray-600 dark:text-gray-400">
      <div className="flex items-center gap-2">
        <Clock className="w-4 h-4" />
        <span className="font-medium">Business Hours</span>
      </div>
      <div className="flex items-center gap-4">
        {timezones.map((tz) => (
          <div key={tz.code} className="flex items-center gap-1">
            <span className="font-mono text-white dark:text-gray-100 min-w-[50px] font-medium">
              {times[tz.code] || new Date().toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit', hour12: false })}
            </span>
            <span className="text-xs text-gray-400 dark:text-gray-400 font-medium">
              {tz.code}
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default WorldClock;

client/src/contexts/theme-context.tsx.content:
import React, { createContext, useContext, useEffect, useState } from 'react';

type Theme = 'light' | 'dark';

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

interface ThemeProviderProps {
  children: React.ReactNode;
}

export const ThemeProvider: React.FC<ThemeProviderProps> = ({ children }) => {
  const [theme, setTheme] = useState<Theme>(() => {
    // Check localStorage first, then system preference
    if (typeof window !== 'undefined') {
      const savedTheme = localStorage.getItem('portfolio-theme') as Theme;
      if (savedTheme) return savedTheme;

      // Check system preference
      return window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light';
    }
    return 'light';
  });

  useEffect(() => {
    const root = document.documentElement;

    if (theme === 'dark') {
      root.classList.add('dark');
    } else {
      root.classList.remove('dark');
    }

    // Save to localStorage
    localStorage.setItem('portfolio-theme', theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

