export const generateATSResumeContent = (format: string): string => {
  const data = resumeData;
  const selectedSummary = data.summary[format] || data.summary.standard;

  let resumeContent = `${data.personalInfo.name.toUpperCase()}
${data.personalInfo.title}
${data.personalInfo.location} | ${data.personalInfo.email} | ${data.personalInfo.phone}`;

  if (data.personalInfo.linkedin) {
    resumeContent += ` | ${data.personalInfo.linkedin}`;
  }

  resumeContent += `

PROFESSIONAL SUMMARY
${selectedSummary}

CORE COMPETENCIES`;

  // Add skills based on format
  if (format === 'technical') {
    resumeContent += `\n‚Ä¢ ${data.skills.technical.slice(0, 4).join(' ‚Ä¢ ')}\n‚Ä¢ ${data.skills.projectManagement.slice(0, 4).join(' ‚Ä¢ ')}\n‚Ä¢ ${data.skills.software.slice(0, 4).join(' ‚Ä¢ ')}`;
  } else if (format === 'wastate') {
    resumeContent += `\n‚Ä¢ ${data.skills.projectManagement.slice(0, 4).join(' ‚Ä¢ ')}\n‚Ä¢ Public sector experience ‚Ä¢ Stakeholder collaboration ‚Ä¢ Process improvement ‚Ä¢ Community service\n‚Ä¢ ${data.skills.technical.slice(0, 3).join(' ‚Ä¢ ')} ‚Ä¢ Government compliance`;
  } else if (format === 'internship') {
    resumeContent += `\n‚Ä¢ Academic Excellence: President's Scholar ‚Ä¢ Project Management Coursework ‚Ä¢ CAPM Candidate\n‚Ä¢ ${data.skills.technical.slice(0, 4).join(' ‚Ä¢ ')}\n‚Ä¢ Strong foundation in ${data.skills.methodologies.slice(0, 3).join(', ')} methodologies`;
  } else if (format === 'assistant') {
    resumeContent += `\n‚Ä¢ ${data.skills.projectManagement.slice(0, 4).join(' ‚Ä¢ ')}\n‚Ä¢ Process documentation ‚Ä¢ Team support ‚Ä¢ Stakeholder coordination ‚Ä¢ Technical communication\n‚Ä¢ ${data.skills.technical.slice(0, 3).join(' ‚Ä¢ ')} ‚Ä¢ ${data.skills.software.slice(0, 2).join(' ‚Ä¢ ')}`;
  } else if (format === 'datacenter') {
    resumeContent += `\n‚Ä¢ ${data.skills.technical.join(' ‚Ä¢ ')}\n‚Ä¢ Datacenter operations ‚Ä¢ 24/7 system monitoring ‚Ä¢ Infrastructure management ‚Ä¢ Disaster recovery\n‚Ä¢ ${data.skills.software.slice(0, 4).join(' ‚Ä¢ ')} ‚Ä¢ ${data.skills.methodologies.slice(0, 3).join(' ‚Ä¢ ')}`;
  } else if (format === 'federal') {
    resumeContent += `\n‚Ä¢ ${data.skills.projectManagement.slice(0, 6).join(' ‚Ä¢ ')}\n‚Ä¢ ${data.skills.methodologies.slice(0, 4).join(' ‚Ä¢ ')}`;
  } else {
    resumeContent += `\n‚Ä¢ ${data.skills.projectManagement.slice(0, 4).join(' ‚Ä¢ ')}\n‚Ä¢ ${data.skills.technical.slice(0, 4).join(' ‚Ä¢ ')}`;
  }

  resumeContent += `\n\nPROFESSIONAL EXPERIENCE\n`;

  // Add experience
  data.experience.forEach(job => {
    resumeContent += `\n${job.title} | ${job.company} | ${job.dates}\n`;
    job.achievements.forEach(achievement => {
      resumeContent += `‚Ä¢ ${achievement}\n`;
    });
  });

  // Add capstone projects
  resumeContent += `\nCAPSTONE PROJECTS\n`;
  data.capstoneProjects.forEach(project => {
    resumeContent += `\n${project.title} | ${project.course}\n`;
    resumeContent += `Technologies: ${project.technologies.join(', ')}\n`;
    project.achievements.forEach(achievement => {
      resumeContent += `‚Ä¢ ${achievement}\n`;
    });
  });

  // Add education
  resumeContent += `\nEDUCATION\n\n${data.education.degree} | ${data.education.school} | Expected 2026\n`;
  resumeContent += `‚Ä¢ ${data.education.honors.join(' | ')}\n`;
  resumeContent += `‚Ä¢ Relevant Coursework: ${data.education.relevantCourses.join(', ')}\n`;

  // Add certifications
  resumeContent += `\nCERTIFICATIONS\n`;
  data.certifications.forEach(cert => {
    resumeContent += `\n‚Ä¢ ${cert.name} - ${cert.issuer} (${cert.date})`;
    if (cert.credentialId) {
      resumeContent += ` - ID: ${cert.credentialId}`;
    }
  });

  // Add technical skills section
  resumeContent += `\n\nTECHNICAL SKILLS\n`;
  resumeContent += `‚Ä¢ Project Management: ${data.skills.projectManagement.slice(0, 6).join(', ')}\n`;
  resumeContent += `‚Ä¢ Technical Systems: ${data.skills.technical.slice(0, 6).join(', ')}\n`;
  resumeContent += `‚Ä¢ Software & Tools: ${data.skills.software.join(', ')}\n`;
